package Control_Statments;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 1;
		while (a < 10) {
			System.out.println("Value of a:" + a);
			a++; // i i+1
		}
		
		System.out.println("while loop exits. value of a is : " + a);

	}

}
